import { useState } from "react";
const reviewList = [
  {
    id: 1,
    name: "Gigi",
    review: "this is an amazing movie",
    votes: 10,
    image: "https://i.pravatar.cc/48?u=153412",
  },
  {
    id: 2,
    name: "Svetlana",
    review: "nice movie!",
    votes: 1,
    image: "https://i.pravatar.cc/48?u=123412",
  },
  {
    id: 3,
    name: "Marya",
    review: "so bad, i am very disappointed!",
    votes: 0,
    image: "https://i.pravatar.cc/48?u=123123",
  },
  {
    id: 4,
    name: "George",
    review: "this page suuucks! 🤬",
    votes: -10,
    image: "https://i.pravatar.cc/48?u=123127",
  },
];

const Review = () => {
  const [reviews, setReviews] = useState(reviewList);
  const [name, setName] = useState("");
  const [image, setImg] = useState("https://i.pravatar.cc/48");
  const [review, setReview] = useState("");
  const [sortBy, setSortBy] = useState("data");
  console.log(sortBy);
  function handleNewComment(e) {
    e.preventDefault();
    if (!name || !image) return;
    const id = crypto.randomUUID();
    const newComment = { name, image, id, votes: 0, review };
    setReviews((reviews) => [...reviews, newComment]);
  }
  function handleVote(value, id) {
    const currId = id;
    console.log(currId);
    const currValue = value === "up" ? 1 : -1;
    console.log(currValue);
    setReviews(
      reviews.map((review) =>
        currId === review.id
          ? { ...review, votes: review.votes + currValue }
          : review
      )
    );
  }
  function handleSort(e) {
    setSortBy(e.target.value);
  }
  let sortedReviews;
  if (sortBy === "date") sortedReviews = reviews;
  if (sortBy === "vote")
    sortedReviews = reviews
      .slice()
      .sort((a, b) => Number(a.votes) - Number(b.votes));
  console.log(sortedReviews);

  return (
    <div className="container">
      <label>Sort By:</label>
      <select onChange={handleSort} value={sortBy} className="select">
        <option value="date">by entry</option>
        <option value="vote">by votes</option>
      </select>
      <ul>
        {sortedReviews.map((review) => (
          <li key={review.id} className="review">
            <img src={review.image} alt={review.name} />
            <p>{review.review}</p>
            <h2>{review.name}</h2>
            <span
              style={
                review.votes > 0
                  ? { color: "green" }
                  : review.votes < 0
                  ? { color: "red" }
                  : { color: "black" }
              }
            >
              {review.votes}
            </span>
            <button
              value="up"
              onClick={(e) => handleVote(e.target.value, review.id)}
              className="like"
            >
              👍
            </button>
            <button
              value="down"
              onClick={(e) => handleVote(e.target.value, review.id)}
              className="dislike"
            >
              👎
            </button>
          </li>
        ))}
      </ul>
      <form className="comment-form" onSubmit={handleNewComment}>
        <label style={{ paddingLeft: "2.5rem" }}> add your comment 🚀</label>
        <input
          onChange={(e) => setName(e.target.value)}
          value={name}
          placeholder="👉...Name"
        />
        <input
          onChange={(e) => setImg(e.target.value)}
          value={image}
          placeholder="📸 ..imageURl"
        />
        <textarea
          value={review}
          onChange={(e) => setReview(e.target.value)}
          placeholder="💬 enter your text here"
        />
        <button>add comment</button>
      </form>
    </div>
  );
};
export default Review;
