import Hero from "./Hero";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

const MovieDetailsView = () => {
  const { id } = useParams();
  const [movieDetails, setMovieDetails] = useState(0);
  const movieUrl = `https://api.themoviedb.org/3/movie/${id}?api_key=38cdc64ccb498aad76f12ea17d9849dc&language=en-US`;
  useEffect(() => {
    fetch(movieUrl)
      .then((response) => response.json())
      .then((data) => {
        setMovieDetails(data);
        console.log(data);
      });
    console.log("make a request!");
  }, [id, movieUrl]);
  const moviePosterImg = `https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`;
  console.log(id);

  return (
    <div>
      <Hero
        backdropImg={movieDetails.backdrop_path}
        text="here are some details about the selected Movie"
      />
      <div className="mx-3 row my-5">
        <div className="col bg-dark text-white">
          <h1> {movieDetails.original_title} </h1>
          <img
            className="img"
            src={moviePosterImg}
            alt={movieDetails.original_title}
          />
        </div>
        <div className="col mx-1 row align-items-center">
          <ul style={{ listStyle: "none" }} className="row">
            {movieDetails.genres &&
              movieDetails.genres.map((genre) => (
                <li className="col-4 mx-2 my-3 genres" key={genre.id}>
                  {genre.name}{" "}
                </li>
              ))}
          </ul>
          <p className="more-info">{movieDetails.overview}</p>
          <span>
            {movieDetails.budget ? `Budget:${movieDetails.budget}$` : null}
          </span>
          <span>votes:{movieDetails.vote_average}/10</span>
          {movieDetails.revenue > 0 && (
            <span>
              Revenue(Profit):{movieDetails.revenue}(
              {movieDetails.revenue - movieDetails.budget})$
            </span>
          )}
        </div>
      </div>
      <Review />
    </div>
  );
};

const reviewsList = [{ id: 1, review: "this is an amazing movie", votes: 2 }];

const Review = () => {
  const [reviews, setReviews] = useState(reviewsList);

  return (
    <div className="container">
      <div className="review">
        <ul>
          {reviews.map((review) => (
            <li>
              <img src=""></img>
              <h3>{review.review}</h3>
              <span>{review.votes}</span>
              <button>upvote</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
export default MovieDetailsView;
