const Hero = ({ text, backgroundImage }) => {
  return (
    <div className="bg-dark text-white p-5">
      <div
        style={{
          backgroundImage: `url(https://image.tmdb.org/t/p/w500${backgroundImage})`,
        }}
      ></div>
      <h1> {text}</h1>
    </div>
  );
};
export default Hero;
