const Hero = ({ backdropImg, children }) => {
  return (
    <>
      {
        <div
          style={backdropImg && { height: "450px" }}
          className="bg-dark text-white p-5 position-relative"
        >
          <div>{children}</div>(
          <div
            className="backdrop-Div"
            style={{
              backgroundImage: `url(https://image.tmdb.org/t/p/original${backdropImg})`,
            }}
          ></div>
        </div>
      }
    </>
  );
};
export default Hero;
